angular.module('starter', ['ionic', 'starter.config', 'starter.controllers', 'starter.api']).run(function($ionicPlatform) {
  $ionicPlatform.ready(function() {
    ionic.Platform.fullScreen();
    if (window.cordova && window.cordova.plugins && window.cordova.plugins.Keyboard) {
      cordova.plugins.Keyboard.hideKeyboardAccessoryBar(!0);
      cordova.plugins.Keyboard.disableScroll(!0)
    }
    if (window.StatusBar) {
      return StatusBar.hide()
    }
  })
}).config(function($stateProvider, $urlRouterProvider) {
  $stateProvider.state('tab', {
    url: '/tab',
    abstract: !0,
    templateUrl: 'templates/tabs.html'
  }).state('tab.dash', {
    url: '/dash',
    views: {
      'tab-dash': {
        templateUrl: 'templates/tab-dash.html',
        controller: 'DashCtrl'
      }
    }
  }).state('tab.photos', {
    cache: !0,
    url: '/photos',
    views: {
      'tab-photos': {
        templateUrl: 'templates/tab-photos.html',
        controller: 'PhotosCtrl'
      }
    }
  });
  $urlRouterProvider.otherwise('/tab/dash')
})
